import { FC, InputHTMLAttributes, useEffect, useState } from "react";
import style from './Home.module.scss';
import kitten from '../../image/Vector.jpg'
import imag from '../../image/image.jpg'
import imagBig from '../../image/imageBig.png'
import getApi, { IUsers } from "../../Api/Api";

const Home: FC = () => {
    const [allUsers, setAllUsers] = useState<IUsers[] | null>(null);
    const filterdNews = allNews?.filter(item => !category || item.newsSite === category);
    
    const [user, setUser] = useState('');
    const handleOnChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setUser(event.target.value)
    };
    const getApiUser = async () => {
        const allUser = await getApi();
        setAllUsers(allUser);
    };
    useEffect(() => {
        getApiUser();
    },[]);

    return (
        <div className={style.home}>
            <div className={style.header}>
                <img className={style.imgGit} src={kitten} alt="icon" />
                <form action="#" className={style.search}>
                    <button type="submit" className={style.btn}>
                        <img className={style.img} src={imag} alt="foto" />
                    </button>
                    <input onChange={handleOnChange} className={style.inputHeader} type="text" placeholder="Enter GitHub username"/>
                </form>     
            </div>
            <div className={style.main}>
                <img className={style.searchBig} src={imagBig} alt="icon"  />
                <h2 className={style.title}>Start with searching a GitHub user</h2>
            </div>
            {allUsers && (
                <div>
                    {allUsers.map(item => (
                        <p>{item.login}</p>
                    ))}
                </div>
            )}
        </div>
    );
};
export default Home;
