import { useEffect, useState } from "react";
import Draggable from "react-draggable";
import { v4 } from 'uuid';
import { randomColor } from 'randomcolor';
import './App.css';

const App = () => {
  const [item, setItem] = useState('');
  const [items, setItems] = useState(
    JSON.parse(localStorage.getItem('items')) || []
  );

  useEffect(() => {
    localStorage.setItem('items', JSON.stringify(items));
  }, [items]);

  const handleOnClick = () => {
    if(item.trim() !== '') {
      const newItem = {
        id: v4(),
        item: item,
        color: randomColor({
          luminosity: 'light'
        }),
        defaultPosition: {
          x: -100,
          y: -100
        }
      }
      setItems((items) => [...items, newItem]);
      setItem('');
    } else {
      alert('Enter something')
    }
  }


  return (
    <div className="App">
      <div className="wrapper">
        <input onChange={(event) => setItem(event.target.value)} className="input" placeholder="Enter something..."/>
        <button onClick={handleOnClick} className="btn">ENTER</button>
      </div>
      {
        items.map((item, index) => {
          return (
            <Draggable key={index} defaultPosition={item.defaultPosition}>
              <div>{item.item}
              </div>
            </Draggable>
          )
        })
      }
    </div>
  );
};

export default App;
